package gen

import "text/template"

var fileHeaderTemplate = template.Must(template.New("File Header").
	Parse(`// Code generated by go generate; DO NOT EDIT.
// This file was generated on {{.Date}}
// using data from https://www.alphavantage.co/documentation

package alphavantage

`))

var categoryTemplate = template.Must(template.New("Category").Parse(`
// Endpoint Category: {{.ReadableName}}
// {{.LinkName}}
//
// {{.Desc}}
`))

var endpointTemplate = template.Must(template.New("Function").Parse(`
{{.DocComment}}
func (a *Alphavantage) Get{{.FuncName}}({{.ArgList}}) (*http.Response, error) {
	function := "{{.EndpointFunction}}"
	params := map[string]string{
{{.QueryParams}}
	}

	return a.client.Query(function, params)
}

`))

var checksumTemplate = template.Must(template.New("Checksum").Parse(`
// Checksum: {{.Checksum}}
`))
